        ]
      },
      isEdit: false,
      rules: {
        type: [
          { required: true, message: '请选择分类', trigger: 'change' }
        ],
        name: [
          { required: true, message: '请输入名称', trigger: 'blur' }
        ],
        orgId: [
          { required: true, message: '请选择所属单位', trigger: 'change' }
        ],
        useOrgId: [
          { required: true, message: '请选择使用单位', trigger: 'change' }
        ],
        companyId: [
          { required: true, message: '请选择所属企业', trigger: 'change' }
        ],
        useCompanyId: [
          { required: true, message: '请选择使用企业', trigger: 'change' }
        ],
        uniqueSerialNumber: [
          { required: true, message: '请输入唯一序号', trigger: 'blur' }
        ],
        tankFieldName: [
          { required: true, message: '请输入罐区名称', trigger: 'blur' }
        ],
        tankGroupName: [
          { required: true, message: '请输入罐组名称', trigger: 'blur' }
        ],
        productionYear: [
          { required: true, message: '请选择投产年份', trigger: 'change' }
        ],
        activatYear: [
          { required: true, message: '请选择建成年份', trigger: 'change' }
        ],
        harbourDistrictId: [
          { required: true, message: '请选择所属港区', trigger: 'change' }
        ]
      },
      loading: false,
      enterpriseList: [],
      orgList: [],
      portAreaList: []
    }
  },
  computed: {
    typeList() {
      return [
        { label: '储罐', value: 1 },
        { label: '重大危险资源', value: 2 },
        { label: '管线', value: 3 },
        { label: '汽车装卸台', value: 4 },
        { label: '火车装车台', value: 5 },
        { label: '堆场', value: 6 },
        { label: '仓库', value: 7 },
        { label: '加油（气）站', value: 8 }
      ]
    }
  },
  async mounted() {
    this.isEdit = this.$route.query.isEdit
    // 获取单位列表
    this.orgList = (await getOrgList()).data
    // 获取企业列表
    this.enterpriseList = (await queryEnterprise({ enterpriseName: '' })).data
    // 港区列表
    this.portAreaList = (await getPortAreaList()).data
    // 查询详情
    if (this.$route.query.id) { // 存在id通过id查详情
      this.id = this.$route.query.id
      let info = await getInfo(this.$route.query.id)
      this.ruleForm = { ...info.data }
      this.$refs.map.init(info.data.geographyInfos)
    }
  },
  methods: {
    // 获取单位名称
    getOrgName() {
      this.ruleForm.orgName = this.orgList.filter(item => item.orgId === this.ruleForm.orgId)[0].orgName
    },
    // 获取使用单位名称
    getUserOrgName() {
      this.ruleForm.useOrgName = this.orgList.filter(item => item.orgId === this.ruleForm.useOrgId)[0].orgName
    },
    // 获取所属企业名称
    getCompanyName() {
      this.ruleForm.companyName = this.enterpriseList.filter(item => item.enterpriseId === this.ruleForm.companyId)[0].enterpriseName
    },
    // 获取使用企业
    getUserCompanyName() {
      this.ruleForm.useCompanyName = this.enterpriseList.filter(item => item.enterpriseId === this.ruleForm.companyId)[0].enterpriseName
    },
    // 获取港区名称
    getHarbourDistrictName() {
      this.ruleForm.harbourDistrict = this.portAreaList.filter(item => item.id === this.ruleForm.harbourDistrictId)[0].portAreaName
    },
    /**
     * 经纬度变化
     */
    confirmMap(val) {
      this.ruleForm.geographyInfos = val
    },
    /**
     * 保存
     */
    submitForm(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          this.save()
        } else {
          return false
        }
      })
    },
    async save() {
      this.ruleForm.geographyInfos.forEach((item, index) => {
        delete item.id
        item.sort = index + 1
      })
      this.ruleForm.productionYear = (this.ruleForm.productionYear && this.ruleForm.productionYear.toString().length === 4) ? this.ruleForm.productionYear+'-01-01' : this.ruleForm.productionYear
      this.ruleForm.activatYear = (this.ruleForm.activatYear && this.ruleForm.activatYear.toString().length === 4) ? this.ruleForm.activatYear+'-01-01' : this.ruleForm.activatYear
      let data = { ...this.ruleForm }
      delete data.harborId
      delete data.harbor
      delete data.deviceName
      this.loading = true
      let res = this.ruleForm.id ? await updateData(data) : await addData(data)
      this.loading = false
      if (res.code === '0') {
        this.returnBack()
      }
    }
  }
}
</script>

