<script>
import { addData, getInfo, updateData } from '@/api/data-management/resources/emergency-rescue'
import { getOrgList, queryEnterprise, getPortAreaList } from '@/api/data-management/resources/other'
import Maptable from '@/components/Maptable/index'
export default {
  components: {
    Maptable
  },
  data() {
    return {
      typeList: [
        { label: '应急物资储备点', value: 1 },
        { label: '应急疏散通道', value: 2 },
        { label: '应急集合点', value: 3 },
        { label: '应急队伍名称', value: 4 },
        { label: '应急事故池', value: 5 },
        { label: '雨水污水收集区', value: 6 },
        { label: '医院', value: 7 },
        { label: '敏感目标', value: 8 }
      ],
      ruleForm: {
        id: '',
        type: '',
        name: '',
        harbourDistrictId: '',
        harbourDistrict: '',
        orgId: '',
        orgName: '',
        companyId: '',
        companyName: '',
        geographyInfos: [
          {
            longitude: '',
            latitude: ''
          }
        ]
      },
      isEdit: false,
      rules: {
        type: [
          { required: true, message: '请选择分类', trigger: 'change' }
        ],
        name: [
          { required: true, message: '请输入名称', trigger: 'blur' }
        ],
        harbourDistrictId: [
          { required: true, message: '请选择所属港区', trigger: 'change' }
        ],
        orgId: [
          { required: true, message: '请选择所属单位', trigger: 'change' }
        ],
        companyId: [
          { required: true, message: '请选择所属企业', trigger: 'change' }
        ],
        putIntoUseTime: [
          { required: true, message: '请选择投用时间', trigger: 'change' }
        ],
        address: [
          { required: true, message: '请输入地址', trigger: 'blur' }
        ],
        population: [
          { required: true, message: '请输入人口', trigger: 'blur' }
        ],
        hospitalLevel:[
          { required: true, message: '请输入医院级别', trigger: 'blur' }
        ]
      },
      loading: false,
      enterpriseList: [],
      orgList: [],
      portAreaList: []
    }
  },
  computed: {
  },
  async mounted() {
    this.isEdit = this.$route.query.isEdit
    // 获取单位列表
    this.orgList = (await getOrgList()).data
    // 获取企业列表
    this.enterpriseList = (await queryEnterprise({ enterpriseName: '' })).data
    // 港区列表
    this.portAreaList = (await getPortAreaList()).data
    // 查询详情
    if (this.$route.query.id) { // 存在id通过id查详情
      this.id = this.$route.query.id
      let info = await getInfo(this.$route.query.id)
      this.ruleForm = { ...info.data }
      this.$refs.map.init(info.data.geographyInfos)
    }
  },
  methods: {
    typeName(value){
      let lable = ''
      this.typeList.forEach(item=>{
        if (item.value===Number(value)){
          lable = item.label
          return false
        }
      })
      return lable
    },
    // 获取港区名称
    getHarbourDistrictName() {
      this.portAreaList.forEach(item=>{
        if (item.id===this.ruleForm.harbourDistrictId){
          this.ruleForm.harbourDistrict = item.portAreaName
          return false
        }
      })
    },
    // 获取单位名称
    getOrgName() {
      this.orgList.forEach(item=>{
        if (item.orgId===this.ruleForm.orgId){
          this.ruleForm.orgName = item.orgName
          return false
        }
      })
    },
    // 获取所属企业名称
    getCompanyName() {
      this.enterpriseList.forEach(item=>{
        if (item.enterpriseId===this.ruleForm.companyId){
          this.ruleForm.companyName = item.enterpriseName
          return false
        }
      })
    },
    /**
     * 经纬度变化
     */
    confirmMap(val) {
      this.ruleForm.geographyInfos = val
    },
    /**
     * 保存
     */
    submitForm(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          this.save()
        } else {
          return false
        }
      })
    },
    async save() {
      this.ruleForm.geographyInfos.forEach((item, index) => {
        delete item.id
        item.sort = index + 1
      })
      this.getOrgName()
      this.getCompanyName()
      this.getHarbourDistrictName()
      let data = { ...this.ruleForm }
      delete data.harborId
      delete data.harbor
      this.loading = true
      let res = this.ruleForm.id ? await updateData(data) : await addData(data)
      this.loading = false
      if (res.code === '0') {
        this.returnBack()
      }
    }
  }
}
</script>

