<template>
  <el-card class="body-card">
    <div
      slot="header"
      class="clearfix"
    >
      <span class="detail-back">
        <el-page-header @back="returnBack" content="消防设施信息详情" />
      </span>
    </div>
    <el-form
      ref="ruleForm"
      size="medium"
      label-position="top"
      :model="ruleForm"
      :rules="rules"
      class="table-top-column-there"
      :disabled="isEdit?false:true"
    >
      <el-form-item
        :label="typeName(ruleForm.type)+'名称:'"
        class="top-form-item"
        prop="name"
      >
        <el-input v-model.trim="ruleForm.name" maxlength="100" placeholder="请输入名称" />
      </el-form-item>
      <el-form-item
        label="分类:"
        class="top-form-item"
        prop="type"
      >
        <el-select
          v-model="ruleForm.type"
          class="top-form-item"
          placeholder="请选择分类"
        >
          <el-option
            v-for="item in typeList"
            :key="item.value"
            :label="item.label"
            :value="item.value"
          />
        </el-select>
      </el-form-item>
      <el-form-item
        label="所属港区:"
        class="top-form-item"
        prop="harbourDistrictId"
      >
        <el-select
          v-model="ruleForm.harbourDistrictId"
          filterable
          class="top-form-item"
          placeholder="请选择所属港区"
          @change="getHarbourDistrictName"
        >
          <el-option
            v-for="item in portAreaList"
            :key="item.id"
            :label="item.portAreaName"
            :value="item.id"
          />
        </el-select>
      </el-form-item>
      <el-form-item
        label="所属单位:"
        class="top-form-item"
        prop="orgId"
      >
        <el-select
          v-model="ruleForm.orgId"
          filterable
          class="top-form-item"
          placeholder="请选择所属单位"
          @change="getOrgName"
        >
          <el-option
            v-for="item in orgList"
            :key="item.orgId"
            :label="item.orgName"
            :value="item.orgId"
          />
        </el-select>
      </el-form-item>
      <div
        v-if="ruleForm.type"
        class="icon-title"
      >
        <span class="span" /> 消防设施信息
      </div>
      <div
        v-if="ruleForm.type"
        class="table-top-column-there"
        style="padding: 0; width: 100%;"
      >
        <el-form-item :label="typeName(ruleForm.type)+'名称:'">
          <el-input
            v-model.trim="ruleForm.name"
            disabled
            prop="name"
          />
        </el-form-item>
        <el-form-item
          v-if="ruleForm.type !== 2"
          label="所属港区:"
          disabled
          prop="harbourDistrictId"
        >
          <el-select
            v-model="ruleForm.harbourDistrictId"
            filterable
            class="top-form-item"
            placeholder="请选择所属港区"
            @change="getHarbourDistrictName"
          >
            <el-option
              v-for="item in portAreaList"
              :key="item.id"
              :label="item.portAreaName"
              :value="item.id"
            />
          </el-select>
        </el-form-item>
        <el-form-item
          v-if="ruleForm.type > 1"
          label="所属企业:"
          prop="companyId"
        >
          <el-select
            v-model="ruleForm.companyId"
            filterable
            class="top-form-item"
            placeholder="请选择"
            @change="getCompanyName"
          >
            <el-option
              v-for="item in enterpriseList"
              :key="item.enterpriseId"
              :label="item.enterpriseName"
              :value="item.enterpriseId"
            />
          </el-select>
        </el-form-item>
      </div>
      <!-- 消防站 -->
      <div
        v-if="ruleForm.type === 1"
        class="table-top-column-there"
        style="padding: 0; width: 100%;"
      >
        <el-form-item
          label="消防车种类:"
          prop="fireEngineType"
        >
          <el-input v-model.trim="ruleForm.fireEngineType" placeholder="请输入消防车种类" maxlength="50" />
        </el-form-item>
        <el-form-item
          label="消防车数量:"
          prop="fireEngineAmount"
        >
          <el-input-number
            v-model="ruleForm.fireEngineAmount"
            :min="0"
            :max="999999999"
            placeholder="请输入消防车数量"
            style="width: 100%;"
          />
        </el-form-item>
        <el-form-item
          label="消防车型号:"
          prop="fireEngineModel"
        >
          <el-input v-model.trim="ruleForm.fireEngineModel" maxlength="100" placeholder="请输入消防车型号" />
        </el-form-item>
        <el-form-item
          label="消防员数量:"
          prop="firemenAmount"
        >
          <el-input-number
            v-model="ruleForm.firemenAmount"
            :min="0"
            :max="999999999"
            placeholder="请输入消防车数量"
            style="width: 100%;"
          />
        </el-form-item>
        <el-form-item
          label="总体消防能力描述:"
          style="width: 100%;"
          prop="fireControlAbility"
        >
          <el-input
            v-model.trim="ruleForm.fireControlAbility"
            type="textarea"
            placeholder="请输入总体消防能力描述"
            :rows="6"
            maxlength="200"
            show-word-limit
          />
        </el-form-item>
      </div>
      <!-- 消防水源 -->
      <div
        v-if="ruleForm.type >= 3"
        class="table-top-column-there"
        style="padding: 0; width: 100%;"
      >
        <el-form-item
          label="储量:"
          prop="reserves"
        >
          <el-input v-model.trim="ruleForm.reserves" maxlength="50" placeholder="请输入储量" />
        </el-form-item>
        <el-form-item
          label="投用时间:"
          prop="putIntoUseTime"
        >
          <el-date-picker
            v-model="ruleForm.putIntoUseTime"
            type="date"
            value-format="yyyy-MM-dd"
            placeholder="选择日期时间"
          />
        </el-form-item>
        <el-form-item
          label="联系人:"
          prop="linkman"
        >
          <el-input v-model.trim="ruleForm.linkman" maxlength="20" placeholder="请输入联系人" />
        </el-form-item>
        <el-form-item
          label="联系电话:"
          prop="phone"
        >
          <el-input v-model.trim="ruleForm.phone" maxlength="11" placeholder="请输入联系电话" />
        </el-form-item>
      </div>
    </el-form>
    <div class="icon-title">
      <span class="span" /> 地理信息
    </div>
    <Maptable
      ref="map"
      :isEdit="isEdit"
      @change="confirmMap"
    />
    <div
      class="backstage-edit-btn"
      style="width: 100%;"
    >
      <el-button
        v-if="isEdit"
        :loading="loading"
        type="primary"
        size="medium"
        @click="submitForm('ruleForm')"
      >
        提交
      </el-button>
      <el-button
        size="medium"
        @click="returnBack"
      >
        返回
      </el-button>
    </div>
  </el-card>
</template>

<script>
import { addData, getInfo, updateData } from '@/api/data-management/resources/fire-fighting'
import { getOrgList, queryEnterprise, getPortAreaList } from '@/api/data-management/resources/other'
import Maptable from '@/components/Maptable/index'
export default {
  components: {
    Maptable
  },
  data() {
    return {
      typeList:[
        { label: '消防站', value: 1 },
        { label: '消防道路', value: 2 },
        { label: '消防水源', value: 3 },
        { label: '泡沫存储点', value: 4 }
      ],
      ruleForm: {
        id: '',
        type: '',
        name: '',
        harbourDistrictId: '',
        harbourDistrict: '',
        orgId: '',
        orgName: '',
        companyId: '',
        companyName: '',
        geographyInfos: [
          {
            longitude: '',
            latitude: ''
          }
        ]
      },
      isEdit: false,
      rules: {
        type: [
          { required: true, message: '请选择分类', trigger: 'change' }
        ],
        name: [
          { required: true, message: '请输入名称', trigger: 'blur' }
        ],
        harbourDistrictId: [
          { required: true, message: '请选择所属港区', trigger: 'change' }
        ],
        orgId: [
          { required: true, message: '请选择所属单位', trigger: 'change' }
        ],
        companyId: [
          { required: true, message: '请选择所属企业', trigger: 'change' }
        ],
        putIntoUseTime: [
          { required: true, message: '请选择投用时间', trigger: 'change' }
        ]
      },
      loading: false,
      enterpriseList: [],
      orgList: [],
      portAreaList: []
    }
  },
  computed: {
  },
  async mounted() {
    this.isEdit = this.$route.query.isEdit
    // 获取单位列表
    this.orgList = (await getOrgList()).data
    // 获取企业列表
    this.enterpriseList = (await queryEnterprise({ enterpriseName: '' })).data
    // 港区列表
    this.portAreaList = (await getPortAreaList()).data
    // 查询详情
    if (this.$route.query.id) { // 存在id通过id查详情
      this.id = this.$route.query.id
      let info = await getInfo(this.$route.query.id)
      this.ruleForm = { ...info.data }
      this.$refs.map.init(info.data.geographyInfos)
    }
  },
  methods: {
    typeName(value){
      let lable = ''
      this.typeList.forEach(item=>{
        if (item.value===Number(value)){
          lable = item.label
          return false
        }
      })
      return lable
    },
    // 获取港区名称
    getHarbourDistrictName() {
      this.ruleForm.harbourDistrict = this.portAreaList.filter(item => item.id === this.ruleForm.harbourDistrictId)[0].portAreaName
    },
    // 获取单位名称
    getOrgName() {
      this.ruleForm.orgName = this.orgList.filter(item => item.orgId === this.ruleForm.orgId)[0].orgName
    },
    // 获取所属企业名称
    getCompanyName() {
      this.ruleForm.companyName = this.enterpriseList.filter(item => item.enterpriseId === this.ruleForm.companyId)[0].enterpriseName
    },
    /**
     * 经纬度变化
     */
    confirmMap(val) {
      this.ruleForm.geographyInfos = val
    },
    /**
     * 保存
     */
    submitForm(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          this.save()
        } else {
          return false
        }
      })
    },
    async save() {
      this.ruleForm.geographyInfos.forEach((item, index) => {
        delete item.id
        item.sort = index + 1
      })
      let data = { ...this.ruleForm }
      delete data.harborId
      delete data.harbor
      this.loading = true
      let res = this.ruleForm.id ? await updateData(data) : await addData(data)
      this.loading = false
      if (res.code === '0') {
        this.returnBack()
      }
    }
  }
}
</script>

