<template>
  <el-card class="body-card">
    <div
      slot="header"
      class="clearfix"
    >
      <span class="detail-back">
        <el-page-header @back="returnBack" content="添加详情" />
      </span>
    </div>
    <el-form
      ref="ruleForm"
      size="medium"
      label-position="top"
      :model="ruleForm"
      :rules="rules"
      class="table-top-column-there"
      :disabled="isEdit?false:true"
    >
      <el-form-item
        :label="typeName(ruleForm.type)+'名称:'"
        class="top-form-item"
        prop="name"
      >
        <el-input v-model.trim="ruleForm.name" maxlength="100" placeholder="请输入名称" />
      </el-form-item>
      <el-form-item
        label="分类:"
        class="top-form-item"
        prop="type"
      >
        <el-select
          v-model="ruleForm.type"
          class="top-form-item"
          placeholder="请选择分类"
        >
          <el-option
            v-for="item in typeList"
            :key="item.value"
            :label="item.label"
            :value="item.value"
          />
        </el-select>
      </el-form-item>
      <el-form-item
        label="所属单位:"
        class="top-form-item"
        prop="orgId"
      >
        <el-select
          v-model="ruleForm.orgId"
          class="top-form-item"
          placeholder="请选择所属单位"
          @change="getOrgName"
        >
          <el-option
            v-for="item in orgList"
            :key="item.orgId"
            :label="item.orgName"
            :value="item.orgId"
          />
        </el-select>
      </el-form-item>
      <el-form-item
        label="使用单位:"
        class="top-form-item"
        prop="useOrgId"
      >
        <el-select
          v-model="ruleForm.useOrgId"
          class="top-form-item"
          placeholder="请选择使用单位"
          @change="getUserOrgName"
        >
          <el-option
            v-for="item in orgList"
            :key="item.orgId"
            :label="item.orgName"
            :value="item.orgId"
          />
        </el-select>
      </el-form-item>
      <el-form-item
        label="唯一序号:"
        prop="uniqueSerialNumber"
      >
        <el-input v-model.trim="ruleForm.uniqueSerialNumber" maxlength="50" placeholder="请输入唯一序号" />
      </el-form-item>
      <div
        v-if="ruleForm.type"
        class="icon-title"
      >
        <span class="span" /> 装卸机械设备信息
      </div>
      <div
        v-if="ruleForm.type"
        class="table-top-column-there"
        style="padding: 0; width: 100%;"
      >
        <el-form-item :label="typeName(ruleForm.type)+'名称:'">
          <el-input
            v-model.trim="ruleForm.name"
            disabled
          />
        </el-form-item>
        <el-form-item
          v-if="ruleForm.type!==4"
          label="唯一序号:"
        >
          <el-input
            v-model.trim="ruleForm.uniqueSerialNumber"
            disabled
          />
        </el-form-item>
        <el-form-item
          label="所属企业:"
          prop="companyId"
        >
          <el-select
            v-model="ruleForm.companyId"
            class="top-form-item"
            placeholder="请选择"
            @change="getCompanyName"
          >
            <el-option
              v-for="item in enterpriseList"
              :key="item.enterpriseId"
              :label="item.enterpriseName"
              :value="item.enterpriseId"
            />
          </el-select>
        </el-form-item>
        <el-form-item
          v-if="ruleForm.type!==4"
          label="使用企业:"
          prop="useCompanyId"
        >
          <el-select
            v-model="ruleForm.useCompanyId"
            class="top-form-item"
            placeholder="请选择使用企业"
            @change="getUserCompanyName"
          >
            <el-option
              v-for="item in enterpriseList"
              :key="item.enterpriseId"
              :label="item.enterpriseName"
              :value="item.enterpriseId"
            />
          </el-select>
        </el-form-item>
      </div>
      <!-- 集装箱装卸桥/门机 -->
      <div
        v-if="ruleForm.type === 1 || ruleForm.type === 5"
        class="table-top-column-there"
        style="padding: 0; width: 100%;"
      >
        <el-form-item
          label="类别:"
          prop="category"
        >
          <el-input v-model.trim="ruleForm.category" maxlength="50" placeholder="请输入类别" />
        </el-form-item>
        <el-form-item
          label="起重量（吨）:"
          prop="payloadCapacity"
        >
          <el-input-number
            v-model="ruleForm.payloadCapacity"
            :precision="3"
            :min="0"
            :max="999999999.999"
            placeholder="请输入起重量（吨）"
            style="width: 100%;"
          />
        </el-form-item>
        <el-form-item
          label="臂幅（米）:"
          prop="swivel"
        >
          <el-input-number
            v-model="ruleForm.swivel"
            :precision="2"
            :min="0"
            :max="999999999.99"
            placeholder="请输入臂幅（米）"
            style="width: 100%;"
          />
        </el-form-item>
        <el-form-item />
        <el-form-item />
      </div>
      <!-- 装/卸船机 -->
      <div
        v-if="ruleForm.type === 2"
        class="table-top-column-there"
        style="padding: 0; width: 100%;"
      >
        <el-form-item
          label="运输货种:"
          prop="transportCargo"
        >
          <el-input v-model.trim="ruleForm.transportCargo" maxlength="50" placeholder="请输入运输货种" />
        </el-form-item>
        <el-form-item
          label="装船效率（吨/小时）:"
          prop="shipmentEfficiency"
        >
          <el-input-number
            v-model="ruleForm.shipmentEfficiency"
            :precision="2"
            :min="0"
            :max="999999999.99"
            placeholder="请输入装船效率（吨/小时）"
            style="width: 100%;"
          />
        </el-form-item>
        <el-form-item />
      </div>
      <!-- 装卸臂 -->
      <div
        v-if="ruleForm.type === 3"
        class="table-top-column-there"
        style="padding: 0; width: 100%;"
      >
        <el-form-item
          label="运输货种:"
          prop="transportCargo"
        >
          <el-input v-model.trim="ruleForm.transportCargo" maxlength="50" placeholder="请输入运输货种" />
        </el-form-item>
        <el-form-item />
      </div>
      <!-- 紧急切断阀 -->
      <div
        v-if="ruleForm.type === 4"
        class="table-top-column-there"
        style="padding: 0; width: 100%;"
      >
        <el-form-item
          label="类别:"
          prop="category"
        >
          <el-input v-model.trim="ruleForm.category" maxlength="50" placeholder="请输入类别" />
        </el-form-item>
        <el-form-item
          label="备注:"
          style="width: 100%;"
          prop="remark"
        >
          <el-input
            v-model.trim="ruleForm.remark"
            :rows="6"
            type="textarea"
            placeholder="请输入备注"
            maxlength="200"
            show-word-limit
          />
        </el-form-item>
      </div>
    </el-form>
    <div class="icon-title">
      <span class="span" /> 地理信息
    </div>
    <Maptable
      ref="map"
      :isEdit="isEdit"
      @change="confirmMap"
    />
    <div
      class="backstage-edit-btn"
      style="width: 100%;"
    >
      <el-button
        v-if="isEdit"
        :loading="loading"
        type="primary"
        size="medium"
        @click="submitForm('ruleForm')"
      >
        提交
      </el-button>
      <el-button
        size="medium"
        @click="returnBack"
      >
        返回
      </el-button>
    </div>
  </el-card>
</template>

<script>
import { addData, getInfo, updateData } from '@/api/data-management/resources/handling-machinery'
import { getOrgList, queryEnterprise } from '@/api/data-management/resources/other'
import Maptable from '@/components/Maptable/index'
export default {
  components: {
    Maptable
  },
  data() {
    return {
      typeList:[
        { label: '集装箱装卸桥', value: 1 },
        { label: '装/卸船机', value: 2 },
        { label: '装卸臂', value: 3 },
        { label: '紧急切断阀', value: 4 },
        { label: '门机', value: 5 }
      ],
      ruleForm: {
        id: '',
        type: '',
        name: '',
        orgId: '',
        orgName: '',
        useOrgId: '',
        useOrgName: '',
        companyId: '',
        companyName: '',
        useCompanyId: '',
        useCompanyName: '',
        uniqueSerialNumber: '',
        geographyInfos: [
          {
            longitude: '',
            latitude: ''
          }
        ]
      },
      isEdit: false,
      rules: {
        type: [
          { required: true, message: '请选择分类', trigger: 'change' }
        ],
        name: [
          { required: true, message: '请输入名称', trigger: 'blur' }
        ],
        orgId: [
          { required: true, message: '请选择所属单位', trigger: 'change' }
        ],
        companyId: [
          { required: true, message: '请选择所属企业', trigger: 'change' }
        ],
        uniqueSerialNumber: [
          { required: true, message: '请输入唯一序号', trigger: 'blur' }
        ],
        putIntoUseTime: [
          { required: true, message: '请选择投用时间', trigger: 'change' }
        ]
      },
      loading: false,
      enterpriseList: [],
      orgList: []
    }
  },
  computed: {
  },
  async mounted() {
    this.isEdit = this.$route.query.isEdit
    // 获取单位列表
    this.orgList = (await getOrgList()).data
    // 获取企业列表
    this.enterpriseList = (await queryEnterprise({ enterpriseName: '' })).data
    // 查询详情
    if (this.$route.query.id) { // 存在id通过id查详情
      this.id = this.$route.query.id
      let info = await getInfo(this.$route.query.id)
      info.data.type = Number(info.data.type)
      this.ruleForm = { ...info.data }
      this.$refs.map.init(info.data.geographyInfos)
    }
  },
  methods: {
    typeName(value){
      let lable = ''
      this.typeList.forEach(item=>{
        if (item.value===Number(value)){
          lable = item.label
          return false
        }
      })
      return lable
    },
    // 获取单位名称
    getOrgName() {
      this.ruleForm.useOrgName = this.orgList.filter(item => item.orgId === this.ruleForm.orgId)[0].orgName
    },
    // 获取使用单位名称
    getUserOrgName() {
      this.ruleForm.orgName = this.orgList.filter(item => item.orgId === this.ruleForm.useOrgId)[0].orgName
    },
    // 获取所属企业名称
    getCompanyName() {
      this.ruleForm.companyName = this.enterpriseList.filter(item => item.enterpriseId === this.ruleForm.companyId)[0].enterpriseName
    },
    // 获取使用企业名称
    getUserCompanyName() {
      this.ruleForm.useCompanyName = this.enterpriseList.filter(item => item.enterpriseId === this.ruleForm.useCompanyId)[0].enterpriseName
    },
    /**
     * 经纬度变化
     */
    confirmMap(val) {
      this.ruleForm.geographyInfos = val
    },
    /**
     * 保存
     */
    submitForm(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          this.save()
        } else {
          return false
        }
      })
    },
    async save() {
      this.ruleForm.geographyInfos.forEach((item, index) => {
        delete item.id
        item.sort = index + 1
      })
      let data = { ...this.ruleForm }
      delete data.harborId
      delete data.harbor
      this.loading = true
      let res = this.ruleForm.id ? await updateData(data) : await addData(data)
      this.loading = false
      if (res.code === '0') {
        this.returnBack()
      }
    }
  }
}
</script>

