<script>
import { addData, getInfo, updateData } from '@/api/data-management/resources/channel-building'
import Maptable from '@/components/Maptable/index'
export default {
  components: {
    Maptable
  },
  data() {
    return {
      typeList:[
        { label: '航段', value: 1 },
        { label: '过河建筑物', value: 2 },
        { label: '临河设施', value: 3 },
        { label: '枢纽', value: 4 }
      ],
      ruleForm: {
        id: '',
        name: '',
        type: '',
        geographyInfos: [
          {
            longitude: '',
            latitude: ''
          }
        ]
      },
      isEdit: false,
      rules: {
        type: [
          { required: true, message: '请选择分类', trigger: 'change' }
        ],
        name: [
          { required: true, message: '请输入名称', trigger: 'blur' }
        ],
        administrativeDivision: [
          { required: true, message: '请输入航段所在行政区划', trigger: 'blur' }
        ],
        originName: [
          { required: true, message: '请输入起点名称', trigger: 'blur' }
        ],
        terminusName: [
          { required: true, message: '请输入终点名称', trigger: 'blur' }
        ],
        buildTime: [
          { required: true, message: '请选择建成时间', trigger: 'blur' }
        ]
      },
      loading: false,
      activityList: ''
    }
  },
  computed: {
  },
  async mounted() {
    this.isEdit = this.$route.query.isEdit
    // 查询详情
    if (this.$route.query.id) { // 存在id通过id查详情
      this.id = this.$route.query.id
      let info = await getInfo(this.$route.query.id)
      this.ruleForm = { ...info.data }
      this.$refs.map.init(info.data.geographyInfos)
    }
  },
  methods: {
    typeName(value){
      let lable = ''
      this.typeList.forEach(item=>{
        if (item.value===Number(value)){
          lable = item.label
          return false
        }
      })
      return lable
    },
    /**
     * 经纬度变化
     */
    confirmMap(val) {
      this.ruleForm.geographyInfos = val
    },
    /**
     * 保存
     */
    submitForm(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          this.save()
        } else {
          return false
        }
      })
    },
    async save() {
      this.ruleForm.geographyInfos.forEach((item, index) => {
        delete item.id
        item.sort = index + 1
      })
      this.ruleForm.buildTime = (this.ruleForm.buildTime && this.ruleForm.buildTime.toString().length===4) ? this.ruleForm.buildTime+'-01-01':''
      let data = { ...this.ruleForm }
      delete data.originIsMasteer
      delete data.terminusIsMasteer
      delete data.shorelineOccupiedLenght
      this.loading = true
      let res = this.ruleForm.id ? await updateData(data) : await addData(data)
      this.loading = false
      if (res.code === '0') {
        this.returnBack()
      }
    }
  }
}
</script>

