<template>
  <el-card class="body-card">
    <div
      slot="header"
      class="clearfix"
    >
      <span class="detail-back">
        <el-page-header @back="returnBack" content="船舶电子档案详情" />
      </span>
    </div>
    <div class="icon-title">
      <span class="span" /> 电子档案
    </div>
    <el-button
      v-if="isEdit"
      type="primary"
      size="mini"
      @click="addData({})"
    >
      添加电子档案文件
    </el-button>
    <el-table
      style="margin-bottom: 20px;"
      :data="info.electronicArchives"
      highlight-current-row
      class="table-border"
    >
      <el-table-column
        label="序号"
        width="55"
      >
        <template v-slot="scope">
          <span>{{ scope.$index + 1 }}</span>
        </template>
      </el-table-column>
      <el-table-column
        label="文件名称"
        prop="fileName"
      />
      <el-table-column
        label="文件编号"
        prop="fileSerialNumber"
      />
      <el-table-column
        label="备注"
        prop="remark"
      />
      <el-table-column label="电子文件">
        <template v-slot="scope">
          <div
            v-for="(item, index) in scope.row.fileList"
            :key="index"
          >
            {{ item.fileName }}
          </div>
        </template>
      </el-table-column>
      <el-table-column
        label="上传时间"
        prop="updateTime"
      />
      <el-table-column
        label="操作"
        align="center"
        width="130"
      >
        <template v-slot="scope">
          <a @click="downloadCodeImg(scope.row.fileList)"><el-button size="mini" type="text">下载</el-button></a>
          <!-- <a
            style="margin: 0 10px"
            @click="addData(scope.row)"
          ><el-button size="mini" type="text">编辑</el-button></a> -->
          <a
            v-if="isEdit"
            style="color: red;"
            @click="removeDate(scope.$index, scope.row.id)"
          ><el-button size="mini" type="text">移除</el-button></a>
        </template>
      </el-table-column>
    </el-table>
    <div class="icon-title">
      <span class="span" /> 船舶基本信息
    </div>
    <div class="list-column-wrap">
      <div class="info-list info-list-two">
        <div class="label">
          船名:
        </div><div class="content">
          {{ info.shipName }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          曾用名:
        </div><div class="content">
          {{ info.usedName }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          船籍港:
        </div><div class="content">
          {{ info.registryPort }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          船舶种类:
        </div><div class="content">
          {{ info.shipType }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          船舶呼号:
        </div><div class="content">
          {{ info.shipCallSign }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          IMO 编号:
        </div><div class="content">
          {{ info.shipImo }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          船体材料:
        </div><div class="content">
          {{ info.hullMaterial }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          造船地点:
        </div><div class="content">
          {{ info.shipBuiltAddrCn }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          造船厂:
        </div><div class="content">
          {{ info.shipYardCn }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          建成日期:
        </div><div class="content">
          {{ info.completionDate }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          改建船厂:
        </div><div class="content">
          {{ info.conversionShipyard }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          改建船地点:
        </div><div class="content">
          {{ info.shipRebuiltAddrCn }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          改建日期:
        </div><div class="content">
          {{ info.rebuildDate }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          船舶总长:
        </div><div class="content">
          {{ info.totalLength }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          船舶型宽:
        </div><div class="content">
          {{ info.width }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          船舶型深:
        </div><div class="content">
          {{ info.depth }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          总吨:
        </div><div class="content">
          {{ info.shipTotalTons }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          净吨:
        </div><div class="content">
          {{ info.netTons }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          参考载重吨:
        </div><div class="content">
          {{ info.netWeightTons }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          主机种类:
        </div><div class="content">
          {{ info.shipEngineType }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          主机数量:
        </div><div class="content">
          {{ info.hostNum }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          主机功率:
        </div><div class="content">
          {{ info.hostPower }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          推进器种类:
        </div><div class="content">
          {{ info.shipPropellerKind }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          推进器数量:
        </div><div class="content">
          {{ info.shipPropellerNum }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          船舶所有人:
        </div><div class="content">
          {{ info.shipOwner }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          船舶所有人地址:
        </div><div class="content">
          {{ info.shipOwnerAddrCn }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          船舶经营人:
        </div><div class="content">
          {{ info.shipOperator }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          船舶经营人地址:
        </div><div class="content">
          {{ info.shipOperatorAddrCn }}
        </div>
      </div>
    </div>
    <div
      class="backstage-edit-btn"
      style="width: 100%;"
    >
      <el-button
        size="medium"
        @click="returnBack"
      >
        返回
      </el-button>
    </div>
  </el-card>
</template>

<script>
import { getInfo,removeData } from '@/api/ship-inspection/electronic-archives/electronic-archives'
import { downloadCodeImg } from '@/utils/index'
export default {
  data() {
    return {
      info: {},
      id: '',
      isEdit: false
    }
  },
  async mounted() {
    this.isEdit = this.$route.query.isEdit ? this.$route.query.isEdit : false
    // 查询详情
    if (this.$route.query.id) { // 存在id通过id查详情
      this.id = this.$route.query.id
      this.info = (await getInfo(this.$route.query.id)).data
      this.info.electronicArchives.forEach(element => {
        element.fileList = JSON.parse(element.fileList) || []
      })
    }
  },
  methods: {
    downloadCodeImg(fileList) {
      fileList.forEach(item => {
        downloadCodeImg(item.filePath)
      })
    },
    removeDate(index,id) {
      this.$confirm('此操作将永久删除该数据, 是否继续?', '操作确认').then(() => {
        removeData(id).then(res => {
          if (res.code === '0') {
            this.$message.success('删除成功!')
            this.info.electronicArchives.splice(index, 1)
          }
        })
      }).catch()
    },
    addData(item) {
      this.$router.push({ name: 'addElectronicArchives', params: { shipInfoId: this.id, ...item }})
    }
  }
}
</script>

<style lang="scss" scoped>
