<template>
  <el-card class="body-card">
    <div
      slot="header"
      class="clearfix"
    >
      <span class="detail-back">
        <el-page-header @back="returnBack" content="企业风险作业单元划分详情" />
      </span>
    </div>
    <div class="icon-title">
      <span class="span" /> 基本资料
    </div>
    <div class="list-column-wrap">
      <div class="info-list info-list-two">
        <div class="label">
          风险辨识范围:
        </div><div class="content">
          {{ ruleForm.scopeName }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          风险作业单元:
        </div><div class="content">
          {{ ruleForm.activityName }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          所属企业名称:
        </div><div class="content">
          {{ ruleForm.enterpriseName }}
        </div>
      </div>
      <div
        v-if="ruleForm.enterpriseType"
        class="info-list info-list-two"
      >
        <div class="label">
          企业类型:
        </div><div class="content">
          {{ getEnterpriseTypeName(ruleForm.enterpriseType) }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          作业区域:
        </div><div class="content">
          {{ ruleForm.taskArea }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          作业场所:
        </div><div class="content">
          {{ ruleForm.taskPlace }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          作业活动:
        </div><div class="content">
          {{ ruleForm.taskActivity }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          更新时间:
        </div><div class="content">
          {{ ruleForm.updateTime }}
        </div>
      </div>
    </div>
    <Maptable
      ref="businessBaseMap"
      :is-edit="isEdit"
      @change="confirmMap"
    />
    <div
      class="backstage-edit-btn"
      style="width: 100%;"
    >
      <el-button
        size="medium"
        @click="returnBack"
      >
        返回
      </el-button>
    </div>
  </el-card>
</template>

<script>
import { getInfo } from '@/api/port-services/risk-identification/risk-activity'
import Maptable from '@/components/Maptable/index'
export default {
  components: {
    Maptable
  },
  data() {
    return {
      ruleForm: {
        id: '',
        scopeId: '',
        activityId: '',
        longitude: '',
        latitude: ''
      },
      isEdit:false,
      geographyInfos:[]
    }
  },
  async mounted() {
    // 查询详情
    if (this.$route.query.id) { // 存在id通过id查详情
      this.id = this.$route.query.id
      let info = await getInfo(this.$route.query.id)
      this.ruleForm = { ...info.data }
      if (info.data && info.data.latitude && info.data.longitude){
        this.geographyInfos = [
          {latitude:info.data.latitude,longitude:info.data.longitude}
        ]
      }
    }
    this.initMap()
  },
  methods: {
    /**
     * 经纬度变化
     */
    confirmMap(val) {
      this.geographyInfos = val
    },
    // 通过企业类型获取类别名称
    getEnterpriseTypeName(key) {
      const list = [
        { key: 1, val: '港口企业' },
        { key: 2, val: '港口服务企业' },
        { key: 3, val: '水路运输企业' },
        { key: 4, val: '水路运输代理企业' },
        { key: 5, val: '船舶管理企业' },
        { key: 6, val: '其他企业' }
      ]
      return list.filter(item => item.key === key)[0].val
    },
    // 初始化天地图
    initMap() {
      this.$nextTick(() => {
        if (this.$refs.businessBaseMap){
          this.$refs.businessBaseMap.init(this.geographyInfos)
        }
      })
    }
  }
}
</script>

<style lang="scss" scoped>
