<template>
  <el-card class="body-card">
    <div
      slot="header"
      class="clearfix"
    >
      <span class="detail-back">
        <el-page-header @back="returnBack" content="风险事件与制险因素详情" />
      </span>
    </div>
    <div class="icon-title">
      <span class="span" /> 风险事件基本信息
    </div>
    <div class="list-column-wrap">
      <div class="info-list info-list-two">
        <div class="label">
          风险作业单元:
        </div><div class="content">
          {{ query.activityName }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          所属企业:
        </div><div class="content">
          {{ query.enterpriseName }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          风险事件:
        </div><div class="content">
          {{ ruleForm.incidentName }}
        </div>
      </div>
      <div class="info-list info-list-two">
        <div class="label">
          风险事件相关描述:
        </div><div class="content">
          {{ ruleForm.incidentDesc }}
        </div>
      </div>
    </div>
    <div class="icon-title">
      <span class="span" /> 制险因素信息
    </div>
    <el-table
      :data="ruleForm.riskControlFactorsInfoVoList"
      stripe
      fit
      highlight-current-row
      class="table-border"
    >
      <el-table-column
        label="序号"
        width="55"
        align="center"
      >
        <template v-slot="scope">
          {{ scope.$index + 1 }}
        </template>
      </el-table-column>
      <el-table-column
        label="制险因素"
        prop="factorsName"
        width="200"
        align="center"
      />
      <el-table-column
        label="制险因素分类"
        width="150"
        align="center"
      >
        <template v-slot="scope">
          {{ scope.row.factorsType===1?'人的因数':(scope.row.factorsType===2?'设备因素':(scope.row.factorsType===3?'环境因素':'管理因素')) }}
        </template>
      </el-table-column>
      <el-table-column
        label="描述"
        prop="factorsDesc"
        align="center"
      />
    </el-table>
    <div
      class="backstage-edit-btn"
      style="width: 100%;"
    >
      <el-button
        size="medium"
        @click="returnBack"
      >
        返回
      </el-button>
    </div>
  </el-card>
</template>

<script>
import { getInfo } from '@/api/port-services/risk-identification/risk-incident'
export default {
  data() {
    return {
      ruleForm: {},
      query: {}
    }
  },
  async mounted() {
    this.query = this.$route.query
    // 查询详情
    if (this.$route.query.id) {
      let info = await getInfo(this.$route.query.id)
      this.ruleForm = { ...info.data }
    }
  }
}
</script>

<style lang="scss" scoped>
